import React, { Component } from 'react';
// import PropTypes from 'prop-types';
import Select from 'react-select';
// import data from '../_data/data.json';

const option = Object.keys(data.mentors).map(item=>{ 
  return {value: data.mentors[item].mentorGithub, label: data.mentors[item].mentorGithub 
  }
  });

export class App extends Component {
  state = {
    selectedMentor: null,
    data: {},
    mentors: {}
  }

  async componentDidMount() {
    try {
      const data = await fetch ('../_data/data.json');
      this.setState({data})

    } catch (e){
      alert(e.toString())
    }
  }

  handleChange = (selectedMentor) => {
    this.setState({ selectedMentor });
    console.log(`Option selected:`, selectedMentor);
  }
  render() {

    const { selectedMentor, data } = this.state;

    const option = Object.keys(data.mentors).map(item=>{ 
      return {value: data.mentors[item].mentorGithub, label: data.mentors[item].mentorGithub 
  }
  });


    return (
      <div>
        <h1>Mentor dashboard</h1>
        <Select
          value={selectedMentor}
          onChange={this.handleChange}
          options={option}
        />
      </div>
    );
  }
}
