import React, { Component, Fragment } from 'react';
// import PropTypes from 'prop-types';
// import Select from 'react-select';
// import data from '../_data/data.json';


export class Table extends Component {
  constructor(props) {
    super()
    this.state = {
      selectedMentor: null,
    }
  }
  
  render() {
    
    // const { selectedMentor} = this.state;
    const {data, mentor} = this.props
// console.log(mentor);
// console.log(data);
    const students = <tr key={'data.mentors[mentor].id'}>{data.mentors[mentor].students.map(stud=> <td key={stud}><a href = {stud.linkGithub}>{stud.name}</a></td>).join('')}</tr>;
    // const tableStudents = data.tasks.map((item) => 
    // <tr key={item.id}>{data.mentors[mentor].students
    //   .map( value => <td className={value.tasks[item.name].className}>1234</td>)
    //   .join('')}</tr>
    // );
  
    return (
        <Fragment>
        <table><tbody>{ students }</tbody></table>
        </Fragment>
    );
  }
}
