import React, { Component } from 'react';
// import PropTypes from 'prop-types';
import Select from 'react-select';
// import data from '../_data/data.json';


export class App extends Component {
  state = {
    selectedMentor: null,
    data: null,
  }

  componentDidMount() {
    fetch('./createdJSON.json')
      .then(response => response.json())
      .then((data) => {
        this.setState({ data });
      });
  }

  handleChange = (selectedMentor) => {
    this.setState({ selectedMentor });
    console.log(`Option selected:`, selectedMentor);
  }
  render() {

    const { selectedMentor, data } = this.state;
console.log(data);
    const option = Object.keys(data.mentors).map(item=>{ 
      return {value: data.mentors[item].mentorGithub, label: data.mentors[item].mentorGithub 
  }
  });

    return (
      <div>
        <h1>Mentor dashboard</h1>
        <Select
          value={selectedMentor}
          onChange={this.handleChange}
          options={option}
        />
      </div>
    );
  }
}
